INSERT INTO `en_posts` (`id`, `title`, `alias`, `description`, `content`, `cate_id`, `thumbnail`, `tags`, `note`, `author_create`, `author_update`, `status`, `create_time`, `update_time`, `hot`, `hot1`, `file_url`, `price`, `sale`, `price_sale`, `view`, `comment`, `meta_title`, `meta_keyword`, `meta_description`) VALUES
(1, 'Bỏ túi những địa chỉ du lịch “sang - xịn” ở Hàn Quốc', 'bo-tui-nhung-dia-chi-du-lich-sang-xin-o-han-quoc', 'Nếu như vùng Kansai có Tokyo mang nét đẹp hiện đại, Kyoto mang vẻ đẹp cổ kính thì Tohoku nằm trên bán đảo Honshu lại mang đến vẻ đẹp tự nhiên đầy chất Nhật Bản, đó là những ngôi nhà của Samurai, lò nấu rượu sake, lễ hội… và những chuyến tàu đi xuyên những tán hoa anh đào.', '&lt;p&gt;L&amp;ecirc;n chuyến t&amp;agrave;u m&amp;ugrave;a Xu&amp;acirc;n, xuy&amp;ecirc;n qua những t&amp;aacute;n anh đ&amp;agrave;o hồng phớt v&amp;agrave; ngắm Nhật Bản thơ mộng trong từng khoảnh khắc&lt;/p&gt;\r\n\r\n&lt;p&gt;Nếu như v&amp;ugrave;ng Kansai c&amp;oacute; Tokyo mang n&amp;eacute;t đẹp hiện đại, Kyoto mang vẻ đẹp cổ k&amp;iacute;nh th&amp;igrave; Tohoku nằm tr&amp;ecirc;n b&amp;aacute;n đảo Honshu lại mang đến vẻ đẹp tự nhi&amp;ecirc;n đầy chất Nhật Bản, đ&amp;oacute; l&amp;agrave; những ng&amp;ocirc;i nh&amp;agrave; của Samurai, l&amp;ograve; nấu rượu sake, lễ hội&amp;hellip; v&amp;agrave; những chuyến t&amp;agrave;u đi xuy&amp;ecirc;n những t&amp;aacute;n hoa anh đ&amp;agrave;o.&lt;/p&gt;\r\n\r\n&lt;p&gt;Bạn c&amp;oacute; thể gh&amp;eacute; c&amp;aacute;c c&amp;ocirc;ng vi&amp;ecirc;n, những l&amp;acirc;u đ&amp;agrave;i hay đền thờ ở Nhật Bản để ngắm hoa anh đ&amp;agrave;o khoe sắc nhưng Tohoku c&amp;oacute; một c&amp;aacute;ch đặc biệt hơn, đ&amp;oacute; l&amp;agrave; những chuyến t&amp;agrave;u cao tốc JR East Pass đi dọc v&amp;ugrave;ng đất Tohoku, luồn dưới những t&amp;aacute;n anh đ&amp;agrave;o nở rộ hay men theo những con đường được phủ một thảm hoa hồng phớt nhẹ. Đặc biệt hơn, ở Tohoku, bạn c&amp;oacute; thể chi&amp;ecirc;m ngưỡng hoa anh đ&amp;agrave;o v&amp;agrave; tuyết rơi c&amp;ugrave;ng một thời điểm, tạo n&amp;ecirc;n bức tranh phong cảnh đầy ấn tượng với sắc hồng rực của sakura tr&amp;ecirc;n nền những ngọn n&amp;uacute;i phủ tuyết trắng v&amp;agrave; bầu trời xanh.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;Chi&amp;ecirc;m ngưỡng khung cảnh lướt ngo&amp;agrave;i cửa sổ&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Với người d&amp;acirc;n Nhật Bản, nếu c&amp;oacute; thời gian rảnh rỗi v&amp;agrave;o m&amp;ugrave;a Xu&amp;acirc;n (giữa th&amp;aacute;ng Tư v&amp;agrave; th&amp;aacute;ng Năm) v&amp;agrave; chọn một h&amp;agrave;nh tr&amp;igrave;nh để được rời khỏi th&amp;agrave;nh phố nhộn nhịp th&amp;igrave; đ&amp;oacute; ch&amp;iacute;nh l&amp;agrave; chuyến đi đến Tohoku. C&amp;ograve;n những du kh&amp;aacute;ch y&amp;ecirc;u th&amp;iacute;ch phong cảnh b&amp;igrave;nh y&amp;ecirc;n của nước Nhật, họ cũng sẽ kh&amp;ocirc;ng ngần ngại chọn Tohoku. Chỉ cần một một tấm v&amp;eacute; t&amp;agrave;u cao tốc, bạn đ&amp;atilde; c&amp;oacute; thể thăm th&amp;uacute; 6 tỉnh của v&amp;ugrave;ng Đ&amp;ocirc;ng Bắc Nhật Bản. Người Nhật chọn h&amp;agrave;nh tr&amp;igrave;nh bằng t&amp;agrave;u cao tốc để đến Tohoku bởi ai cũng th&amp;iacute;ch được trải nghiệm cảm gi&amp;aacute;c ngồi tr&amp;ecirc;n con t&amp;agrave;u lao vun v&amp;uacute;t với khung cảnh đẹp như tranh vẽ đang lướt ngo&amp;agrave;i cửa sổ.&lt;/p&gt;\r\n\r\n&lt;p&gt;Tr&amp;ecirc;n chuyến t&amp;agrave;u m&amp;ugrave;a Xu&amp;acirc;n đặc biệt ấy, du kh&amp;aacute;ch đ&amp;atilde; chọn ra 6 điểm ngắm hoa để dừng ch&amp;acirc;n. Đầu ti&amp;ecirc;n phải kể đến l&amp;agrave; l&amp;acirc;u đ&amp;agrave;i Hirosaki thuộc tỉnh Aomori. L&amp;acirc;u đ&amp;agrave;i Hirosaki được chọn l&amp;agrave; 100 điểm tuyệt vời nhất ngắm hoa anh đ&amp;agrave;o ở Nhật Bản. Danh hiệu n&amp;agrave;y cũng đủ để n&amp;oacute;i l&amp;ecirc;n sự đặc biệt bởi với h&amp;agrave;ng ng&amp;agrave;n điểm ngắm hoa anh đ&amp;agrave;o khắp đất nước, để lọt v&amp;agrave;o top 100 chắc chắn cũng phải rất đặc biệt.&lt;/p&gt;\r\n\r\n&lt;p&gt;Với những ai y&amp;ecirc;u th&amp;iacute;ch kh&amp;ocirc;ng gian rộng lớn th&amp;igrave; sẽ dừng ch&amp;acirc;n ở c&amp;ocirc;ng vi&amp;ecirc;n th&amp;agrave;nh cổ Funaoka, tỉnh Miyagi. C&amp;ocirc;ng vi&amp;ecirc;n nằm tr&amp;ecirc;n đỉnh đồi nhỏ Hitome Senbon Zakura nổi tiếng với khung cảnh Trước mắt l&amp;agrave; ng&amp;agrave;n c&amp;acirc;y anh đ&amp;agrave;o với 1.000 c&amp;acirc;y anh đ&amp;agrave;o dọc theo s&amp;ocirc;ng Shiroishi v&amp;agrave; tầm nh&amp;igrave;n tuyệt vời ra n&amp;uacute;i Zao. Đ&amp;acirc;y cũng l&amp;agrave; địa điểm thường xuy&amp;ecirc;n tổ chức lễ hội hoa anh đ&amp;agrave;o h&amp;agrave;ng đầu nước Nhật.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;https://saigontourist.net/uploads/tuyendiem/AnPham/NhatBan/Hoa-Anh-Dao.jpg&quot; /&gt;Những c&amp;acirc;y anh đ&amp;agrave;o ở c&amp;ocirc;ng vi&amp;ecirc;n Hitome Senbon Zakura nổi bật với l&amp;agrave; d&amp;atilde;y n&amp;uacute;i tuyết Zao ở ph&amp;iacute;a xa&lt;/p&gt;\r\n\r\n&lt;p&gt;Đối với những ai y&amp;ecirc;u mến hoa, nhất định sẽ kh&amp;ocirc;ng chần chừ m&amp;agrave; sẽ gh&amp;eacute; qua c&amp;ocirc;ng vi&amp;ecirc;n Hanamiyama, nơi được mệnh danh l&amp;agrave; Ngọn đồi hoa ở tỉnh Fukushima. C&amp;ocirc;ng vi&amp;ecirc;n n&amp;agrave;y sẽ khiến bạn như đi lạc giữa một thi&amp;ecirc;n đường hoa rực rỡ. V&amp;agrave;o th&amp;aacute;ng Tư, bạn c&amp;oacute; thể c&amp;ugrave;ng l&amp;uacute;c ngắm hoa anh đ&amp;agrave;o, hoa mận v&amp;agrave; hoa mai Mỹ với nền l&amp;agrave; ngọn n&amp;uacute;i Azuma phủ tuyết trắng. Fukushima c&amp;ograve;n c&amp;oacute; khu vực Miharu Takizakura, nơi sở hữu hơn 10.000 c&amp;acirc;y anh đ&amp;agrave;o với c&amp;aacute;c nh&amp;aacute;nh lan rộng đến 20m theo mọi hướng, nh&amp;igrave;n tựa như một th&amp;aacute;c nước.&lt;/p&gt;\r\n\r\n&lt;p&gt;Ở thị trấn samurai Kakunodate, c&amp;oacute; một đại lộ với hơn 150 c&amp;acirc;y anh đ&amp;agrave;o c&amp;agrave;nh rủ mọc ven bờ s&amp;ocirc;ng, đ&amp;aacute;nh dấu ranh giới giữa những ng&amp;ocirc;i nh&amp;agrave; samurai m&amp;agrave;u x&amp;aacute;m đơn giản v&amp;agrave; sắc hoa rực rỡ của thi&amp;ecirc;n nhi&amp;ecirc;n. Từ ba c&amp;acirc;y anh đ&amp;agrave;o đầu ti&amp;ecirc;n được trồng v&amp;agrave;o do năm 1662 do một phụ nữ thuộc d&amp;ograve;ng d&amp;otilde;i qu&amp;yacute; tộc mang từ Kyoto đến tỉnh Akita, giờ đ&amp;acirc;y đ&amp;atilde; trở th&amp;agrave;nh con đường hoa anh đ&amp;agrave;o nổi tiếng.&lt;/p&gt;\r\n\r\n&lt;p&gt;Đến Iwate, kh&amp;ocirc;ng thể bỏ qua c&amp;ocirc;ng vi&amp;ecirc;n Kitakima Tenshochi nằm b&amp;ecirc;n d&amp;ograve;ng s&amp;ocirc;ng thơ mộng Kitakami, một trong 100 c&amp;ocirc;ng vi&amp;ecirc;n trồng hoa anh đ&amp;agrave;o lớn nhất Nhật Bản. Đến đ&amp;acirc;y, bạn c&amp;oacute; thể đứng tr&amp;ecirc;n c&amp;acirc;y cầu bắc ngang qua s&amp;ocirc;ng để nh&amp;igrave;n ngắm to&amp;agrave;n cảnh c&amp;ocirc;ng vi&amp;ecirc;n từ tr&amp;ecirc;n cao để&amp;nbsp; thấy hết vẻ đẹp của n&amp;oacute;.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;https://saigontourist.net/uploads/tuyendiem/AnPham/NhatBan/Kitakami-Tenshochi-Park.jpg&quot; /&gt;C&amp;ocirc;ng vi&amp;ecirc;n Kitakima Tenshochi&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;Những chuyến t&amp;agrave;u hạnh ph&amp;uacute;c&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Nhắc tới Nhật Bản l&amp;agrave; nhắc tới những chuyến t&amp;agrave;u cao tốc thần kỳ với độ ch&amp;iacute;nh x&amp;aacute;c về giờ giấc tuyệt đối, tuy nhi&amp;ecirc;n để chi&amp;ecirc;m ngưỡng hết vẻ đẹp của v&amp;ugrave;ng Tohoku, đa số mọi&amp;nbsp; người chọn c&amp;aacute;ch di chuyển bằng những chuyến t&amp;agrave;u địa phương. Với tấm v&amp;eacute; JR East Pass, bạn c&amp;oacute; thể l&amp;ecirc;n được hầu hết c&amp;aacute;c chuyến t&amp;agrave;u n&amp;agrave;y miễn ph&amp;iacute;. Ngo&amp;agrave;i việc gi&amp;uacute;p tiết kiệm chi ph&amp;iacute; cho việc di chuyển th&amp;igrave; những chuyến t&amp;agrave;u n&amp;agrave;y c&amp;ograve;n sở hữu phong c&amp;aacute;ch thiết kế &amp;ldquo;c&amp;oacute; một kh&amp;ocirc;ng hai&amp;rdquo;, mang đậm tinh thần s&amp;aacute;ng tạo v&amp;agrave; vui vẻ của người Nhật Bản.&lt;/p&gt;\r\n\r\n&lt;p&gt;Nếu ngo&amp;agrave;i cửa sổ l&amp;agrave; m&amp;ugrave;a Xu&amp;acirc;n với những đường hầm hoa anh đ&amp;agrave;o hay khung cảnh n&amp;uacute;i đồi rực rỡ th&amp;igrave; b&amp;ecirc;n trong toa t&amp;agrave;u lại được thiết kế v&amp;ocirc; số phong c&amp;aacute;ch kh&amp;aacute;c nhau để bất cứ ai bước v&amp;agrave;o cũng phải trầm trồ.&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;T&amp;agrave;u FruiTea Fukushima - Qu&amp;aacute;n c&amp;agrave;-ph&amp;ecirc; du lịch: Khu vực Fukushima được mệnh danh l&amp;agrave; vua vườn c&amp;acirc;y ăn quả của Nhật Bản với nhiều loại tr&amp;aacute;i c&amp;acirc;y như nho, t&amp;aacute;o, đ&amp;agrave;o, l&amp;ecirc;. Toa t&amp;agrave;u Fruitea được thiết kế theo m&amp;ocirc; h&amp;igrave;nh một qu&amp;aacute;n c&amp;agrave;-ph&amp;ecirc; du lịch với kh&amp;ocirc;ng gian sang trọng, thanh lịch để ngắm cảnh v&amp;agrave; thưởng thức c&amp;aacute;c m&amp;oacute;n tr&amp;aacute;ng miệng, đồ uống độc đ&amp;aacute;o. V&amp;eacute; l&amp;ecirc;n t&amp;agrave;u đ&amp;atilde; bao gồm một set tr&amp;agrave; với 2 b&amp;aacute;nh tart tr&amp;aacute;i c&amp;acirc;y hoặc b&amp;aacute;nh ngọt, tr&amp;agrave; tr&amp;aacute;i c&amp;acirc;y v&amp;agrave; nước tr&amp;aacute;i c&amp;acirc;y.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;https://saigontourist.net/uploads/tuyendiem/AnPham/NhatBan/FruiTea-Interior.jpg&quot; /&gt;T&amp;agrave;u FruiTea Fukushima với khung cảnh l&amp;agrave; một qu&amp;aacute;n c&amp;agrave;-ph&amp;ecirc; xinh xắn&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;https://saigontourist.net/uploads/tuyendiem/AnPham/NhatBan/FruiTea-Food.jpg&quot; /&gt;Bữa ăn đầy m&amp;agrave;u sắc tr&amp;ecirc;n t&amp;agrave;u FruiTea Fukushima&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;https://saigontourist.net/uploads/tuyendiem/AnPham/NhatBan/FruiTea-Exterior.jpg&quot; /&gt;T&amp;agrave;u FruiTea Fukushima với những qu&amp;aacute;n c&amp;agrave;-ph&amp;ecirc; xinh xắn b&amp;ecirc;n trong&lt;/p&gt;\r\n\r\n&lt;p&gt;T&amp;agrave;u Resort Shirakami - Nghỉ dưỡng ngắm biển: T&amp;agrave;u Resort Shirakami được nhiều h&amp;agrave;nh kh&amp;aacute;ch trẻ trung y&amp;ecirc;u th&amp;iacute;ch v&amp;igrave; thiết kế rộng r&amp;atilde;i với những khung cửa sổ rộng th&amp;iacute;ch hợp cho việc thưởng ngoạn cảnh đẹp. M&amp;agrave;u sắc chủ đạo trong toa t&amp;agrave;u l&amp;agrave; m&amp;agrave;u xanh l&amp;aacute; v&amp;agrave; cam đỏ của v&amp;ugrave;ng cao nguy&amp;ecirc;n Shirakami Sanchi. Tr&amp;ecirc;n suốt h&amp;agrave;nh tr&amp;igrave;nh c&amp;ograve;n c&amp;oacute; c&amp;aacute;c m&amp;agrave;n m&amp;uacute;a rối, biểu diễn nhạc cụ c&amp;ugrave;ng m&amp;oacute;n cơm hộp bento được chế biến từ nguy&amp;ecirc;n liệu địa phương.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;https://saigontourist.net/uploads/tuyendiem/AnPham/NhatBan/Resort-Shirakami.jpg&quot; /&gt;T&amp;agrave;u Resort Shirakami nổi bật tr&amp;ecirc;n khung cảnh m&amp;ugrave;a Xu&amp;acirc;n&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;https://saigontourist.net/uploads/tuyendiem/AnPham/NhatBan/shutterstock_642570679.jpg&quot; /&gt;Chuyến t&amp;agrave;u Resort Shirakami đang đi xuy&amp;ecirc;n qua c&amp;ocirc;ng vi&amp;ecirc;n Funaoka Joshi ở tỉnh Miyagi&lt;/p&gt;\r\n\r\n&lt;p&gt;Pok&amp;eacute;mon with You - Những ch&amp;uacute; Pok&amp;eacute;mon vui nhộn: Nếu l&amp;agrave; fan h&amp;acirc;m mộ của truyện tranh hay hoạt h&amp;igrave;nh Nhật Bản th&amp;igrave; bạn nhất định phải l&amp;ecirc;n con t&amp;agrave;u n&amp;agrave;y khi đến Tohoku. T&amp;agrave;u Pok&amp;eacute;mon with You được thiết kế nhằm mang lại nhiều tiếng cười cho trẻ nhỏ ở Tohoku sau trận động đất s&amp;oacute;ng thần v&amp;agrave;o năm 2011 cũng như tạo n&amp;ecirc;n một kh&amp;ocirc;ng gian để cha mẹ v&amp;agrave; con c&amp;aacute;i vui chơi c&amp;ugrave;ng Pikachu, tận hưởng khoảng thời gian vui vẻ tr&amp;ecirc;n h&amp;agrave;nh tr&amp;igrave;nh. Với tạo h&amp;igrave;nh như một ch&amp;uacute; Pikachu khổng lồ c&amp;ugrave;ng nội thất v&amp;agrave; ph&amp;ograve;ng vui chơi đều ngập tr&amp;agrave;n gấu b&amp;ocirc;ng Pikachu, chuyến đi của bạn chắc chắn sẽ v&amp;ocirc; c&amp;ugrave;ng đ&amp;aacute;ng nhớ.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;https://saigontourist.net/uploads/tuyendiem/AnPham/NhatBan/Pokemon-Exterior-2.jpg&quot; /&gt;T&amp;agrave;u Pok&amp;eacute;mon with you c&amp;oacute; ngoại h&amp;igrave;nh m&amp;agrave;u v&amp;agrave;ng&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;https://saigontourist.net/uploads/tuyendiem/AnPham/NhatBan/Pokemon-Interior-2.jpg&quot; /&gt;Chuyến t&amp;agrave;u Pok&amp;eacute;mon with you l&amp;agrave; tấm v&amp;eacute; để bạn trở về tuổi thơ&lt;/p&gt;\r\n\r\n&lt;p&gt;Toreiyu Tsubasa - Chuyến đi thư gi&amp;atilde;n: &amp;ldquo;Toreiyu&amp;rdquo; l&amp;agrave; sự pha trộn c&amp;aacute;ch ph&amp;aacute;t &amp;acirc;m tiếng Nhật của từ tiếng Anh &amp;ldquo;train&amp;rdquo; (torein) v&amp;agrave; từ tiếng Ph&amp;aacute;p &amp;ldquo;olesil &amp;ldquo;(soreiyu) c&amp;oacute; nghĩa l&amp;agrave; &amp;quot;mặt trời&amp;quot;. Tr&amp;ecirc;n t&amp;agrave;u l&amp;uacute;c n&amp;agrave;o cũng c&amp;oacute; sẵn tatami (chiếu ngồi truyền thống ở Nhật Bản), quầy bar với rượu sake, rượu vang, nước tr&amp;aacute;i c&amp;acirc;y ủ đặc sản địa phương v&amp;agrave; hồ nước n&amp;oacute;ng ng&amp;acirc;m ch&amp;acirc;n thư gi&amp;atilde;n được đặt đối diện cửa sổ để ngắm cảnh. Chuyến t&amp;agrave;u Toreiyu Tsubasa sẽ đem lại cho bạn cảm gi&amp;aacute;c như đang đi dạo quanh một thị trấn suối nước n&amp;oacute;ng.&lt;/p&gt;\r\n\r\n&lt;p&gt;V&amp;agrave; khi những lớp tuyết cuối c&amp;ugrave;ng đang bắt đầu tan, Tohoku lại bắt đầu đ&amp;oacute;n m&amp;ugrave;a Xu&amp;acirc;n trở lại, những b&amp;uacute;p anh đ&amp;agrave;o cũng sẽ dần đ&amp;acirc;m chồi để rồi nở bung rực rỡ từ giữa th&amp;aacute;ng Tư. Nếu bạn c&amp;oacute; hẹn với m&amp;ugrave;a hoa anh đ&amp;agrave;o th&amp;igrave; Tohoku v&amp;agrave; những chuyến t&amp;agrave;u đầy m&amp;agrave;u sắc đang chờ đ&amp;oacute;n bạn. Khung cảnh thần ti&amp;ecirc;n sẽ lướt ngo&amp;agrave;i cửa sổ.&lt;/p&gt;\r\n\r\n&lt;p&gt;M&amp;aacute;ch nhỏ: Bạn c&amp;oacute; thể trải nghiệm những chuyến t&amp;agrave;u m&amp;ugrave;a Xu&amp;acirc;n bằng c&amp;aacute;ch mua v&amp;eacute; t&amp;agrave;u JR East Pass khu vực Tohoku. V&amp;eacute; c&amp;oacute; thời hạn sử dụng 14 ng&amp;agrave;y t&amp;iacute;nh từ l&amp;uacute;c sử dụng lần đầu ti&amp;ecirc;n v&amp;agrave; c&amp;oacute; thể sử dụng v&amp;eacute; 5 ng&amp;agrave;y bất kỳ trong 14 ng&amp;agrave;y. Bạn c&amp;oacute; thể đặt chỗ c&amp;aacute;c phương tiện giao th&amp;ocirc;ng 30 ng&amp;agrave;y trước ng&amp;agrave;y khởi h&amp;agrave;nh tại: https://www.eki-net.com/pc/jreast-shinkansen-reservation/English/wb/common/Menu/Menu.aspx. Đặc biệt, bạn sẽ được giảm ngay 1.000 y&amp;ecirc;n khi mua v&amp;eacute; tại Việt Nam.&lt;/p&gt;\r\n', ',1,', 'MC1.jpg', '', '', 9, 9, 1, '1557512416', '1557514210', 1, 1, NULL, NULL, 0, NULL, NULL, '[]', '', '', ''),
(2, '5 điều không thể bỏ lỡ ở đảo nhân tại Odaiba', '5-dieu-khong-the-bo-lo-o-dao-nhan-tai-odaiba', 'Odaiba thu hút khách du lịch bởi tính đa dạng, thỏa mãn đủ mọi nhu cầu tham quan, mua sắm và vui chơi', '&lt;p&gt;Nằm trong vịnh Tokyo, h&amp;ograve;n đảo nh&amp;acirc;n tạo Odaiba (c&amp;ograve;n được gọi tắt l&amp;agrave; Daiba) được nối liền với th&amp;agrave;nh phố Tokyo bởi một đường ray t&amp;agrave;u hỏa. Những năm gần đ&amp;acirc;y, Odaiba trở th&amp;agrave;nh một điểm thu h&amp;uacute;t kh&amp;aacute;ch du lịch trong v&amp;agrave; ngo&amp;agrave;i nước bởi t&amp;iacute;nh đa dạng, thỏa m&amp;atilde;n đủ mọi nhu cầu tham quan, mua sắm v&amp;agrave; vui chơi của tất cả mọi người.&lt;/p&gt;\r\n\r\n&lt;p&gt;Để tham quan Odaiba, bạn chỉ cần khoảng một ng&amp;agrave;y tr&amp;ecirc;n đảo. Tuy nhi&amp;ecirc;n, lựa chọn chơi g&amp;igrave;, xem g&amp;igrave; chắc chắn sẽ l&amp;agrave;m kh&amp;ocirc;ng &amp;iacute;t du kh&amp;aacute;ch đau đ&amp;acirc;u. Sau đ&amp;acirc;y l&amp;agrave; 5 điều bạn kh&amp;ocirc;ng thể bỏ lỡ khi đến Odaiba:&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;&lt;em&gt;1. Ngắm cầu Cầu Vồng (Rainbow Bridge)&lt;/em&gt;&lt;/strong&gt;&lt;br /&gt;\r\nCầu Cầu Vồng nối liền Tokyo v&amp;agrave; đảo Odaiba, được xem l&amp;agrave; một trong những biểu tượng của th&amp;agrave;nh phố.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;https://saigontourist.net/uploads/destination/an-pham/Website%20content/Autumn_2018/Odaiba/Liberty-in-Odaiba_470754800.jpg&quot; /&gt;&lt;strong&gt;&lt;em&gt;Tượng Nữ thần Tự Do phi&amp;ecirc;n bản Odaiba với cầu Cầu Vồng ở ph&amp;iacute;a sau.&lt;/em&gt;&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;&lt;em&gt;2. Trải nghiệm v&amp;ograve;ng quanh khổng lồ ở Palette Town&lt;/em&gt;&lt;/strong&gt;&lt;br /&gt;\r\nĐ&amp;acirc;y l&amp;agrave; một trong những v&amp;ograve;ng quay cao nhất ở Nhật Bản, với độ cao 115m. V&amp;agrave;o những ng&amp;agrave;y đẹp trời, khi l&amp;ecirc;n đến đỉnh của v&amp;ograve;ng quay, bạn c&amp;oacute; thể thấy to&amp;agrave;n cảnh th&amp;agrave;nh phố Tokyo.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;https://saigontourist.net/uploads/destination/an-pham/Website%20content/Autumn_2018/Odaiba/Odaiba_745889395.jpg&quot; /&gt;&lt;strong&gt;&lt;em&gt;V&amp;ograve;ng quay khổng lồ cai 115m ở Palette Town.&lt;/em&gt;&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;&lt;em&gt;3. Tham quan showroom cực đỉnh của Toyota&lt;/em&gt;&lt;/strong&gt;&lt;br /&gt;\r\nToyota l&amp;agrave; h&amp;atilde;ng xe h&amp;agrave;ng đầu Nhật Bản, với showroom xe hơi ho&amp;agrave;nh tr&amp;aacute;ng MEGA WEB tọa lạc b&amp;ecirc;n trong Palette Town. MEGA WEB mở cửa miễn ph&amp;iacute; cho du kh&amp;aacute;ch tham quan v&amp;agrave; trải nghiệm c&amp;ocirc;ng chế t&amp;aacute;c xe hơi của Toyota c&amp;ugrave;ng những chuẩn mực về sự ho&amp;agrave;n hảo v&amp;agrave; độ ch&amp;iacute;nh x&amp;aacute;c kiểu Nhật.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;https://saigontourist.net/uploads/destination/an-pham/Website%20content/Autumn_2018/Odaiba/Odaiba-island_1039842097.jpg&quot; /&gt;&lt;img alt=&quot;&quot; src=&quot;https://saigontourist.net/uploads/destination/an-pham/Website%20content/Autumn_2018/Odaiba/Odaiba-island_1039842097.jpg&quot; /&gt;&lt;strong&gt;&lt;em&gt;Một mẫu xe đua Toyota trưng b&amp;agrave;y tại showroom&lt;/em&gt;&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;&lt;em&gt;4. Du h&amp;agrave;nh vượt thời gian tại Miraikan&lt;/em&gt;&lt;/strong&gt;&lt;br /&gt;\r\nBảo t&amp;agrave;ng Quốc gia Khoa học v&amp;agrave; Đổi mới tr&amp;ecirc;n đảo Odaiba c&amp;ograve;n c&amp;oacute; t&amp;ecirc;n l&amp;agrave; Miraikan. Gi&amp;aacute;m đốc bảo t&amp;agrave;ng l&amp;agrave; nh&amp;agrave; du h&amp;agrave;nh vũ trụ người Nhật Mamoru Mori. Ở bảo t&amp;agrave;ng, du kh&amp;aacute;ch c&amp;oacute; thể trải nghiệm cảm gi&amp;aacute;c du h&amp;agrave;nh vượt thời gian hoặc du h&amp;agrave;nh v&amp;agrave;o vũ trụ c&amp;ugrave;ng những th&amp;iacute; nghiệm khoa học th&amp;uacute; vị v&amp;agrave; hấp dẫn.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;https://saigontourist.net/uploads/destination/an-pham/Website%20content/Autumn_2018/Odaiba/Museum-of-the-Future_1072962932.jpg&quot; /&gt;&lt;em&gt;&lt;strong&gt;Du h&amp;agrave;nh vượt thời gian tại bảo t&amp;agrave;ng Miraikan&lt;/strong&gt;&lt;/em&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;em&gt;&lt;strong&gt;5. Mua sắm thả ga&lt;/strong&gt;&lt;/em&gt;&lt;br /&gt;\r\nOdaiba c&amp;oacute; rất nhiều trung t&amp;acirc;m mua sắm lớn, tập trung đủ mọi thương hiệu nổi tiếng tr&amp;ecirc;n thế giới v&amp;agrave; h&amp;agrave;ng nội địa Nhật Bản. Một trong những trung t&amp;acirc;m mua sắm bạn kh&amp;ocirc;ng thể bỏ qua l&amp;agrave; Venus Fort &amp;ndash; trung t&amp;acirc;m mua sắm trong nh&amp;agrave; theo phong c&amp;aacute;ch ch&amp;acirc;u &amp;Acirc;u thời trung cổ.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;img alt=&quot;&quot; src=&quot;https://saigontourist.net/uploads/destination/an-pham/Website%20content/Autumn_2018/Odaiba/Odaiba_433280737.jpg&quot; /&gt;&lt;strong&gt;&lt;em&gt;Điểm chụp h&amp;igrave;nh &amp;ldquo;hot&amp;rdquo; nhất b&amp;ecirc;n trong trung t&amp;acirc;m mua sắm Venus Fort&lt;/em&gt;&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;&lt;em&gt;M&amp;aacute;ch nhỏ:&amp;nbsp;&lt;/em&gt;&lt;/strong&gt;H&amp;agrave;nh tr&amp;igrave;nh kh&amp;aacute;m ph&amp;aacute; đất nước Nhật Bản c&amp;ugrave;ng h&amp;ograve;n đảo nh&amp;acirc;n tạo Odaiba nổi tiếng tr&amp;ecirc;n c&amp;aacute;c chuyến đi khởi h&amp;agrave;nh từ HCM, H&amp;agrave; Nội. Tham khảo chương tr&amp;igrave;nh chi tiết tại website:&amp;nbsp;&lt;em&gt;&lt;a href=&quot;https://www.saigontourist.net/vi/tour-nuoc-ngoai/1237/du-lich-nhat-ban-tokyo-kamakura-nui-phu-sy&quot;&gt;https://www.saigontourist.net/vi/tour-nuoc-ngoai/1237/du-lich-nhat-ban-tokyo-kamakura-nui-phu-sy&lt;/a&gt;&lt;/em&gt;&lt;/p&gt;\r\n', ',1,', 'MY.jpg', '', '', 9, NULL, 1, '1557512851', NULL, 1, 