(76, ',6,31,30,', 'Du Lịch Tây Bắc Mùa Hoa Ban Nở - Hà Nội - Sơn La - Điện Biên - Mộc Châu - Hà Nội-4N3D-Thứ 5 hàng Tuần', 'du-lich-tay-bac-mua-hoa-ban-no-ha-noi-son-la-dien-bien-moc-chau-ha-noi-4n3d-thu-5-hang-tuan', NULL, NULL, '3650000', '', NULL, NULL, NULL, 0, 0, 0, 1, '0', 0, NULL, '[]', 'Từ tháng 3 trở đi,ở Tây Bắc khắp nơi chỗ nào cũng thấy hoa Ban - phải chăng là vì loài hoa đã đi vào huyền thoại, đi vào những câu chuyện tình yêu, hay thậm chí là đi vào cả những câu chuyện bên bếp lửa mỗi đêm. Người dân tộc Thái có câu hát:“Đôi ta yêu nhau không tính mùa ban nở - Không thấy ngày ban tàn - Không tính tháng, không tính năm - Mãi mãi như mùa hoa đầu đôi ta yêu nhau”...Nếu lên Tây Bắc vào dịp xuân sang, rất có thể bạn sẽ được dự lễ hội “Kin pang then” của người Thái Trắng và lễ hội “Kin chiêng bók may” của người Thái Đen, bạn sẽ được đắm mình qua những cuộc vui nồng nàn, ý vị và đậm đà bản sắc, để tạm quên đi những âu lo trĩu nặng kiếp con người.\r\n', 0, 0, '', NULL, 1554455601, 9, 1557510605, 9, 'MC1.jpg', NULL, 0, NULL, NULL, NULL, 0, 0, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, NULL, NULL, NULL, '', '4 ngày 2 đêm', 'oto', 'Hà Nội', 'null', NULL, 2, '2'),
(77, ',1,6,30,', 'Du lịch Mộc Châu - Hòa mình vào không gian xanh đồi núi', 'du-lich-moc-chau-hoa-minh-vao-khong-gian-xanh-doi-nui', NULL, NULL, '1250000', '', NULL, NULL, NULL, 0, 0, 0, 1, '0', 0, NULL, '[]', '\"Bỏ lại những guồng quay vội vã và đầy mệt mỏi của cuộc sống đô thị  phồn hoa… Lên với Mộc Châu, Quý khách sẽ cảm nhận được sự ấm áp về tấm lòng hiếu khách và thân thiện của người dân vùng Cao Nguyên này,….tạo cho du khách cảm giác vui vẻ, thoải mái, thư giãn khi đặt chân đến chốn này…”', 0, 0, '', NULL, 1555230689, 9, 1557510596, 9, 'CLC.jpg', NULL, 0, NULL, NULL, NULL, 0, 0, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, NULL, NULL, NULL, 'nón du lịch', '3 ngày 2 đêm', 'ô tô', 'Hà Nội', '[\"S\\u01a1n La\"]', NULL, NULL, '3'),
(78, ',1,6,30,', 'Du lịch Mai Châu - Pù Luông - Mai Châu 3 ngày 2 đêm', 'du-li-ch-mai-chau-pu-luong-mai-chau-3-nga-y-2-dem', NULL, NULL, '2450000', '', NULL, NULL, NULL, 0, 0, 0, 1, '0', 0, NULL, 'null', 'Trong tour du lịch Mai Châu – Pù Luông, quý khách sẽ được tìm hiểu những danh thắng của thung lũng xinh đẹp này. Tìm hiểu những nét văn hóa, phong tục tập quán đặc sắc của dân tộc Thái, hay thưởng thức những món ăn mang hương vị núi rừng, uống rượu cần, tham gia ca múa truyền thống cũng những chàng trai cô gái Thái. Khám phá khu bảo tồn thiên nhiên Pù Luông phong phú đa dạng với nhiều loài động thực vật quý hiếm.', 0, 0, '', NULL, 1555231150, 9, 1557510550, 9, 'cover-1513334084-163-width1920height1113.jpg', NULL, 0, NULL, NULL, NULL, 0, 0, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, NULL, NULL, NULL, NULL, 'nón du lịch', '3 ngày 2 đêm', 'ô tô', 'Hà Nội', '[\"S\\u01a1n La\"]', NULL, 2, '3');
