(1, ',1,7,19,', 'PHÚ YÊN - QUY NHƠN (BÃI XẾP - GÀNH ĐÁ ĐĨA - LẶN NGẮM SAN HÔ ĐẢO KỲ CO - EO GIÓ) TGT', 'phu-yen-quy-nhon-bai-xep-ganh-da-dia-lan-ngam-san-ho-dao-ky-co-eo-gio-tgt', NULL, NULL, '5490000', 4990000, NULL, NULL, NULL, 0, 0, 0, 1, '0', 0, NULL, NULL, 'Nói đến dải đất Miền Trung thân thương là nói đến những thành phố quyến rũ, thơ mộng bên bờ biển cùng hàng trăm ốc đảo hoang sơ, kỳ vĩ giữa trùng khơi. Phú Yên và Quy Nhơn là hai địa danh thu hút lượng lớn khách du lịch thời gian gần đây qua những thước phim vô cùng mãn nhãn, say đắm lòng người bên những thắng cảnh thiên nhiên hùng vĩ trong ‘‘Tôi thấy hoa vàng trên cỏ xanh’’ của đạo diễn Victor Vũ. Sự thanh bình và khí trời tự nhiên nơi địa linh nhân kiệt chắc chắn sẽ làm xiêu lòng người lữ khách phương xa. Hãy để Fiditour tạo nên những kỉ niệm đẹp cùng quý khách!', 0, 0, '', NULL, 1559573485, 9, 1559640104, 9, 'phuyen-quynhon/QuyNhon-PhuYen04.jpg', NULL, 0, NULL, NULL, NULL, 0, 0, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, NULL, NULL, NULL, '', '3 Ngày - 2 Đêm', 'Máy bay', 'Hà Nội', '[\"Ph\\u00fa Qu\\u1ed1c\"]', '2019-06-21', '2019-06-23', 1, NULL, '1', 0),
(2, ',1,8,25,', 'HÀ NỘI - NINH BÌNH - CHÙA BÁI ĐÍNH - TRÀNG AN - HẠ LONG - TUẦN CHÂU TGT', 'ha-noi-ninh-binh-chua-bai-dinh-trang-an-ha-long-tuan-chau-tgt', NULL, NULL, '7790000', 6990000, NULL, NULL, NULL, 0, 0, 0, 1, '0', 0, NULL, NULL, 'Từ độ mang gươm đi mở cõi - Ngàn năm thương nhớ đất Thăng Long\". Hà Nội, mảnh đất ngàn năm văn hiến, với bích đào Nhật Tân, chợ hoa Hàng Lược, với hoa sữa nồng nàn, với quán cóc liêu xiêu, với đường Cổ Ngư xưa chầm chậm bước ta về, với đủ bốn mùa xuân-hạ-thu-đông…  \r\nChuyến đi còn đưa du khách đến với một di sản thiên nhiên thế giới Hạ Long kỳ vĩ, một Tuần Châu rực rỡ trên mặt biển Đông và một non thiêng Chùa Ba Vàng \"ngôi chùa có chính điện lớn nhất Đông Dương\"', 0, 0, '', NULL, 1559574030, 9, 1559640144, 9, 'halong/HaLong01_-_Thumnail.jpg', NULL, 0, NULL, NULL, NULL, 0, 0, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, NULL, NULL, NULL, '', '4 Ngày - 3 Đêm', 'Máy bay', 'Hồ Chí Minh', '[\"Ninh B\\u00ecnh\",\"H\\u00e0 N\\u1ed9i\",\"H\\u1ea1 Long\"]', '2019-07-04', '2019-07-07', 1, NULL, '1', 1),
(3, ',2,9,', 'BALI - ĐẢO LEMBONGAN, INDONESIA (NGẮM BÌNH MINH CỦA THẾ GIỚI - ĐI BỘ TRÊN BIỂN) TGT', 'bali-dao-lembongan-indonesia-ngam-binh-minh-cua-the-gioi-di-bo-tren-bien-tgt', NULL, NULL, '11990000', 11400000, NULL, NULL, NULL, 0, 0, 0, 1, '0', 0, NULL, NULL, 'Bali, một ốc đảo nhỏ xinh đẹp, thơ mộng ngoài khơi Ấn Độ Dương của Indonesia, được mệnh danh là “ Đảo Thần”, “Bình minh của thế giới” hay “Thiên đường nhiệt đới”, du khách sẽ bị hấp dẫn ngay bởi những bãi biển dài đầy cát, những thửa ruộng bậc thang ngút ngàn, những ngọn nút lửa cao chót vót trên 3.000m nằm êm đềm như đang tận hưởng vẻ đẹp tuyệt trần mà thiên nhiên đã ban tặng cho Bali, hay những cánh rừng nhiệt đới hoang dã,', 0, 0, '', NULL, 1559574413, 9, NULL, NULL, 'indonexia/TanahLotThumnail.jpg', NULL, 0, NULL, NULL, NULL, 0, 0, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, NULL, NULL, NULL, '', '4 Ngày - 3 Đêm', 'Máy bay', 'Hà Nội', 'null', '2019-06-14', '2019-06-17', NULL, NULL, '1', NULL),
(4, ',2,', 'NGA (MOSCOW - SAINT PETERSBURG) TGT', 'nga-moscow-saint-petersburg-tgt', NULL, NULL, '57990000', 52999000, NULL, NULL, NULL, 0, 0, 0, 1, '0', 0, NULL, NULL, 'Nước Nga – ngoài mùa đông với nhiệt độ thường xuyên xuống hơn 10 độ âm, mùa thu với những lá vàng lá đỏ thì còn có một mùa rất đặc biệt - gọi là ‘mùa đêm trắng”.', 0, 0, '', NULL, 1559574682, 9, 1559640086, 9, 'nga/du-lich-nga-tour-dem-trang-thumnail.jpg', NULL, 0, NULL, NULL, NULL, 0, 0, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, NULL, NULL, NULL, '', '8 Ngày - 7 Đêm', 'Máy bay', 'Hồ Chí Minh', 'null', '2019-07-04', '2019-07-11', 1, NULL, '1', 0),
(5, ',22,2,9,', 'TOUR PHƯỢNG HOÀNG CỔ TRẤN FREE & EASY', 'tour-phuong-hoang-co-tran-free-easy', NULL, NULL, '9990000', 0, NULL, NULL, NULL, 0, 0, 0, 1, '0', 0, NULL, NULL, 'Phượng Hoàng Cổ Trấn đang là điểm đến làm cho các tín đồ du lịch chết mê chết mệt cái chốn bồng lai tiên cảnh như truyện phim kiếm hiệp này là có thật. Phượng Hoàng là tên một cổ trấn của Trung Quốc nằm tại huyện Phượng Hoàng, Châu tự trị người Thổ Gia, người Miêu Tương Tây ở phía tây tỉnh Hồ Nam. Nó cách Cát Thủ khoảng 53 km, cách địa cấp thị Hoài Hóa khoảng 92 km và cách địa cấp thị Trương Gia Giới khoảng 280 km.', 0, 0, '', NULL, 1559575070, 9, 1559639942, 9, 'phuonghoangco/1.jpg', NULL, 0, NULL, NULL, NULL, 0, 0, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, NULL, NULL, NULL, '', '5 ngày 4 đêm', 'Máy bay', 'Hồ Chí Minh', 'null', '2019-06-05', '2019-06-13', 1, NULL, '1', 1);
