INSERT INTO `vi_product_detail` (`id`, `id_product`, `full_info`, `tags`, `id_field`, `form_field`, `form_default`, `meta_title`, `meta_keyword`, `meta_description`, `related_product`, `alias`, `dichvu`, `dieukhoan`, `ghichu`) VALUES
(1, 1, '&lt;p&gt;LỊCH TR&amp;Igrave;NH TOUR&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;Chuyến bay dự kiến: Vietjet Air&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;em&gt;S&amp;agrave;i G&amp;ograve;n &amp;ndash; Tuy H&amp;ograve;a: 06:15 &amp;ndash; 07:30&lt;/em&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;em&gt;Quy Nhơn &amp;ndash; S&amp;agrave;i G&amp;ograve;n: 19:35 &amp;ndash; 20:40&lt;/em&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;br /&gt;\r\n&lt;strong&gt;NG&amp;Agrave;Y 01: TP. HỒ CH&amp;Iacute; MINH&amp;nbsp;&amp;ndash; PH&amp;Uacute; Y&amp;Ecirc;N &amp;ndash; G&amp;Agrave;NH Đ&amp;Aacute; ĐĨA &amp;ndash; TUY HO&amp;Agrave;&amp;nbsp;(Ăn&amp;nbsp;trưa, tối)&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;img alt=&quot;&quot; src=&quot;https://www.fiditour.com/Upload/images2016/images/Th%c3%a1p%20Nh%e1%ba%a1n.jpg&quot; /&gt;&lt;/strong&gt;&lt;br /&gt;\r\n&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;Qu&amp;yacute; kh&amp;aacute;ch tập trung tại&amp;nbsp;&lt;strong&gt;Ga đi trong nước&amp;nbsp;&lt;/strong&gt;s&amp;acirc;n bay&amp;nbsp;&lt;strong&gt;T&amp;acirc;n Sơn Nhất&lt;/strong&gt;. Hướng dẫn vi&amp;ecirc;n l&amp;agrave;m thủ tục đ&amp;aacute;p chuyến bay đến&amp;nbsp;&lt;strong&gt;Ph&amp;uacute; Y&amp;ecirc;n - &amp;ldquo;Xứ sở Hoa V&amp;agrave;ng Tr&amp;ecirc;n Cỏ Xanh&amp;rdquo;&lt;/strong&gt;.&lt;/p&gt;\r\n\r\n&lt;p&gt;Đến Ph&amp;uacute; Y&amp;ecirc;n, xe đ&amp;oacute;n đo&amp;agrave;n tại s&amp;acirc;n bay Tuy H&amp;ograve;a. Ăn s&amp;aacute;ng, theo cung đường ven biển tuyệt đẹp, qu&amp;yacute; kh&amp;aacute;ch khởi h&amp;agrave;nh tham quan:&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;&lt;strong&gt;Nh&amp;agrave; Thờ Mằng Lăng&lt;/strong&gt;&amp;nbsp;&lt;em&gt;- với lịch sử gần 120 năm tồn tại, đ&amp;acirc;y được coi nh&amp;agrave; thờ cổ nhất Ph&amp;uacute; Y&amp;ecirc;n&amp;nbsp;v&amp;agrave; l&amp;acirc;u đời nhất Việt Nam với nhiều chứng t&amp;iacute;ch lịch sử quan trọng. Đặc biệt l&amp;agrave; cuốn gi&amp;aacute;o l&amp;yacute;&amp;ldquo;Ph&amp;eacute;p giảng t&amp;aacute;m ng&amp;agrave;y&amp;rdquo;bằng&amp;nbsp;tiếng Việt đầu ti&amp;ecirc;n của Linh mục Đắc Lộ &amp;ndash; người khai sinh chữ quốc ngữ ở Việt Nam&lt;/em&gt;.&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;&lt;strong&gt;G&amp;agrave;nh Đ&amp;aacute; Đĩa&lt;/strong&gt;&amp;nbsp;&lt;em&gt;- một tuyệt t&amp;aacute;c địa chất kỳ th&amp;uacute; v&amp;agrave; độc đ&amp;aacute;o của Việt Nam với những khối lăng trụ huyền thạch h&amp;agrave;ng trăm năm xếp chồng l&amp;ecirc;n nhau như một tổ ong khổng lồ b&amp;igrave;nh y&amp;ecirc;n b&amp;ecirc;n bờ biển.&lt;/em&gt;&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;p&gt;Ăn trưa tại nh&amp;agrave; h&amp;agrave;ng địa phương. Buổi chiều qu&amp;yacute; kh&amp;aacute;ch tham quan:&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;&lt;em&gt;&lt;strong&gt;B&amp;atilde;i X&amp;eacute;p (G&amp;agrave;nh X&amp;eacute;p)&lt;/strong&gt;&amp;nbsp;- b&amp;atilde;i c&amp;aacute;t v&amp;agrave;ng &amp;oacute;ng k&amp;eacute;o d&amp;agrave;i giữa hai mũi đ&amp;aacute; lớn nh&amp;ocirc; ra biển như một thi&amp;ecirc;n đường hoang sơ với cảnh quan tuyệt sắc. Ấn tượng hơn cả l&amp;agrave; b&amp;atilde;i cỏ rộng m&amp;ecirc;nh m&amp;ocirc;ng tr&amp;ecirc;n đỉnh G&amp;agrave;nh X&amp;eacute;p xuất hiện đầy thơ mộng trong bộ phim&amp;nbsp;&lt;strong&gt;&amp;ldquo;T&amp;ocirc;i thấy hoa v&amp;agrave;ng tr&amp;ecirc;n cỏ xanh&amp;rdquo;&amp;nbsp;&lt;/strong&gt;đ&amp;atilde; trở th&amp;agrave;nh điểm đến kh&amp;ocirc;ng thể bỏ qua khi đến với Ph&amp;uacute; Y&amp;ecirc;n.&lt;/em&gt;&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;p&gt;Ăn tối. Qu&amp;yacute; kh&amp;aacute;ch tự do kh&amp;aacute;m ph&amp;aacute; th&amp;agrave;nh phố về đ&amp;ecirc;m.&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;Nghỉ đ&amp;ecirc;m tại Tuy Ho&amp;agrave;.&lt;/strong&gt;&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;strong&gt;NG&amp;Agrave;Y 02: QUY NHƠN - B&amp;Igrave;NH ĐỊNH - ĐẤT V&amp;Otilde; TRỜI VĂN&amp;nbsp;(Ăn s&amp;aacute;ng, trưa)&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;img alt=&quot;&quot; src=&quot;https://www.fiditour.com/Upload/images2016/images/g%c3%a0nh%20%c4%91%c3%a1%20dia(6).jpg&quot; /&gt;&lt;/strong&gt;&lt;br /&gt;\r\n&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;Ăn s&amp;aacute;ng tại kh&amp;aacute;ch sạn. Trả ph&amp;ograve;ng, Đo&amp;agrave;n khởi h&amp;agrave;nh đi Quy Nhơn, tr&amp;ecirc;n đường đi Xe v&amp;agrave; Hdv Đưa đo&amp;agrave;n vượt&amp;nbsp;&lt;strong&gt;Cầu Thị Nại&lt;/strong&gt;&amp;nbsp;&amp;ndash;&amp;nbsp;&lt;em&gt;c&amp;acirc;y cầu vượt biển d&amp;agrave;i nhất Việt Nam với chiều d&amp;agrave;i gần 2,5km. Tại đ&amp;acirc;y Qu&amp;yacute; Kh&amp;aacute;ch c&amp;oacute; thể dừng ch&amp;acirc;n, ngắm những tia nắng v&amp;agrave;ng rắc tr&amp;ecirc;n Đầm Thị Nại&lt;/em&gt;.&lt;br /&gt;\r\nQu&amp;yacute; kh&amp;aacute;ch tham quan&amp;nbsp;&lt;strong&gt;Khu du lịch Ghềnh R&amp;aacute;ng &amp;ndash; Ti&amp;ecirc;n Sa&lt;/strong&gt;: Sở hữu một dải sơn thạch chạy dọc theo bờ c&amp;aacute;t trắng trải d&amp;agrave;i b&amp;ecirc;n l&amp;agrave;n biển xanh biếc, đ&amp;atilde; từ l&amp;acirc;u được biết đến như thắng cảnh tuyệt mỹ bậc nhất v&amp;ugrave;ng đất v&amp;otilde; B&amp;igrave;nh Định n&amp;agrave;y.&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;&lt;strong&gt;B&amp;atilde;i tắm Ho&amp;agrave;ng Hậu&lt;/strong&gt;&amp;nbsp;hay c&amp;ograve;n gọi l&amp;agrave; B&amp;atilde;i Trứng&amp;nbsp;&lt;em&gt;&amp;ndash; nơi Nam Phương ho&amp;agrave;ng hậu, vị ho&amp;agrave;ng hậu cuối c&amp;ugrave;ng của chế độ phong kiến Việt Nam đ&amp;atilde; ưu &amp;aacute;i lựa chọn l&amp;agrave;m b&amp;atilde;i tắm ri&amp;ecirc;ng. Những b&amp;atilde;i đ&amp;aacute; h&amp;igrave;nh trứng k&amp;igrave; ảo tập trung rất nhiều ngay dưới những v&amp;aacute;ch n&amp;uacute;i tạo n&amp;ecirc;n một khung cảnh đẹp v&amp;agrave; lạ đến ngỡ ng&amp;agrave;ng.&amp;nbsp;&lt;/em&gt;&amp;nbsp;&lt;/li&gt;\r\n	&lt;li&gt;&lt;strong&gt;Ng&amp;ocirc;i mộ thi sĩ H&amp;agrave;n Mạc Tử&lt;/strong&gt;&amp;nbsp;&lt;em&gt;- con người t&amp;agrave;i hoa bạc mệnh gắn liền với những &amp;aacute;ng thơ t&amp;igrave;nh bất hủ. V&amp;agrave;o những năm cuối đời, H&amp;agrave;n Mặc Tử trải qua nỗi đau đớn do căn bệnh phong qu&amp;aacute;i &amp;aacute;c tại trại phong Quy H&amp;ograve;a, B&amp;igrave;nh Định v&amp;agrave; sau khi qua đời, ng&amp;ocirc;i mộ để tưởng niệm &amp;ocirc;ng được x&amp;acirc;y n&amp;ecirc;n giữa khung cảnh biển trời bao la, thơ mộng của Ghềnh R&amp;aacute;ng - Ti&amp;ecirc;n Sa n&amp;agrave;y&lt;/em&gt;.&amp;nbsp;&lt;/li&gt;\r\n	&lt;li&gt;&lt;strong&gt;Nh&amp;agrave; thờ Ghềnh R&amp;aacute;ng&lt;/strong&gt;&amp;nbsp;&lt;em&gt;- Qu&amp;yacute; kh&amp;aacute;ch sẽ kh&amp;aacute; bất ngờ với lối kiến tr&amp;uacute;c đẹp, cổng gỗ đơn sơ, kh&amp;ocirc;ng gian b&amp;ecirc;n trong xanh m&amp;aacute;t của khu nhờ thờ n&amp;agrave;y. Bước v&amp;agrave;o đ&amp;acirc;y, ngo&amp;agrave;i được t&amp;igrave;m hiểu th&amp;ecirc;m về kiến tr&amp;uacute;c c&amp;aacute;c nh&amp;agrave; thờ gi&amp;aacute;o phật c&amp;ograve;n c&amp;oacute; thể cảm nhận khung cảnh y&amp;ecirc;n b&amp;igrave;nh v&amp;agrave; ấm &amp;aacute;p, đơn giản v&amp;agrave; h&amp;agrave;i h&amp;ograve;a b&amp;ecirc;n trong.&amp;nbsp;&lt;/em&gt;&lt;/li&gt;\r\n	&lt;li&gt;Ngo&amp;agrave;i ra, Qu&amp;yacute; kh&amp;aacute;ch c&amp;ograve;n c&amp;oacute; thể chứng kiến&amp;nbsp;&lt;strong&gt;nghệ thuật thi ph&amp;aacute;p bằng b&amp;uacute;t lửa do nghệ nh&amp;acirc;n Dzũ&lt;/strong&gt;&amp;nbsp;&lt;strong&gt;Kha&lt;/strong&gt;&amp;nbsp;thực hiện v&amp;agrave; mua về l&amp;agrave;m kỉ niệm những tấm gỗ th&amp;ocirc;ng được khắc l&amp;ecirc;n từng lời thơ, vần chữ s&amp;acirc;u sắc của nh&amp;agrave; thơ H&amp;agrave;n Mặc Tử.&amp;nbsp;&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;p&gt;Ăn trưa tại nh&amp;agrave; h&amp;agrave;ng, chiều Xe đưa Qu&amp;yacute; kh&amp;aacute;ch đến với đất v&amp;otilde; B&amp;igrave;nh Định tham quan&amp;nbsp;&lt;strong&gt;Bảo T&amp;agrave;ng Quang Trung lịch sử h&amp;agrave;o h&amp;ugrave;ng d&amp;acirc;n tộc&lt;/strong&gt;&amp;nbsp;- một trong những bảo t&amp;agrave;ng lịch sử nổi tiếng ở B&amp;igrave;nh Định, nơi đ&amp;acirc;y gắn liền với khởi nghĩa T&amp;acirc;y Sơn l&amp;agrave; một trong những cuộc khởi nghĩa giải ph&amp;oacute;ng d&amp;acirc;n tộc th&amp;agrave;nh c&amp;ocirc;ng nhất ở nước ta&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;&lt;em&gt;Nơi trưng b&amp;agrave;y những di vật quan trọng của phong tr&amp;agrave;o T&amp;acirc;y Sơn như c&amp;acirc;y me cổ thụ, giếng nước xưa, 9 pho tượng d&amp;aacute;t v&amp;agrave;ng..v.v.&lt;/em&gt;&lt;/li&gt;\r\n	&lt;li&gt;&lt;em&gt;Xem biểu diễn v&amp;otilde; thuật v&amp;agrave; trống trận T&amp;acirc;y Sơn. Một b&amp;agrave;i trống trận T&amp;acirc;y Sơn gồm 3 hồi: Xuất trận, xung trận &amp;ndash; c&amp;ocirc;ng th&amp;agrave;nh, ca kh&amp;uacute;c khải ho&amp;agrave;n&amp;hellip;, kh&amp;ocirc;ng hề c&amp;oacute; hồi trống trận lui qu&amp;acirc;n như những b&amp;agrave;i trống trận th&amp;ocirc;ng thường. (chi ph&amp;iacute; tự t&amp;uacute;c )&lt;/em&gt;&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;p&gt;Ăn tối tại nh&amp;agrave; h&amp;agrave;ng địa phương, Qu&amp;yacute; kh&amp;aacute;ch tự do thưởng thức đặc sản địa phương v&amp;agrave; kh&amp;aacute;m ph&amp;aacute; th&amp;agrave;nh phố biển về đ&amp;ecirc;m.&lt;br /&gt;\r\n&lt;strong&gt;Nghỉ đ&amp;ecirc;m tại Quy Nhơn.&lt;/strong&gt;&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;strong&gt;NG&amp;Agrave;Y 03: LẶN NGẮM SAN H&amp;Ocirc; KỲ CO&amp;nbsp;&amp;ndash; EO GI&amp;Oacute;&amp;nbsp;&amp;ndash; TP.HỒ CH&amp;Iacute; MINH (Ăn s&amp;aacute;ng, trưa)&lt;br /&gt;\r\n&lt;img alt=&quot;&quot; src=&quot;https://www.fiditour.com/Upload/images2016/images/55-bien-ky-co(2).jpg&quot; /&gt;&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Ăn s&amp;aacute;ng tại kh&amp;aacute;ch sạn. Qu&amp;yacute; kh&amp;aacute;ch bắt đầu h&amp;agrave;nh tr&amp;igrave;nh kh&amp;aacute;m ph&amp;aacute; đảo JEJU của Việt Nam&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;Qu&amp;yacute; kh&amp;aacute;ch l&amp;ecirc;n Cano qua&amp;nbsp;&lt;strong&gt;B&amp;atilde;i tắm Kỳ Co&lt;/strong&gt;&amp;nbsp;(đi đường bộ nếu thời tiết xấu) &amp;ndash;&amp;nbsp;&lt;em&gt;Một b&amp;atilde;i tắm c&amp;ograve;n hoang sơ, trong sạch. Tại đ&amp;acirc;y, qu&amp;yacute; kh&amp;aacute;ch c&amp;oacute; thể tận mắt chứng kiến một cảnh đẹp thi&amp;ecirc;n nhi&amp;ecirc;n tuyệt đẹp, l&amp;agrave;m say đắm biết bao l&amp;ograve;ng người. Với m&amp;agrave;u nước trong xanh, b&amp;atilde;i c&amp;aacute;t trắng d&amp;agrave;i v&amp;agrave; mịn, những cơn s&amp;oacute;ng biển &amp;ecirc;m &amp;aacute;i ch&amp;iacute;nh l&amp;agrave; điểm đến l&amp;yacute; tưởng thu h&amp;uacute;t nhiều du kh&amp;aacute;ch trong thời gian gần đ&amp;acirc;y.một trong những b&amp;atilde;i biển đẹp nứt l&amp;ograve;ng du kh&amp;aacute;ch gần xa . Qu&amp;yacute; kh&amp;aacute;ch nhận khu vực ri&amp;ecirc;ng, tự do tắm biển.&amp;nbsp;&lt;/em&gt;&lt;/li&gt;\r\n	&lt;li&gt;Cano đưa Qu&amp;yacute; kh&amp;aacute;ch đến&amp;nbsp;&lt;strong&gt;B&amp;atilde;i San H&amp;ocirc;&lt;/strong&gt;&amp;nbsp;hay c&amp;ograve;n gọi&amp;nbsp;&lt;strong&gt;b&amp;atilde;i Dứa&lt;/strong&gt;, qu&amp;yacute; kh&amp;aacute;ch tự do lặn biển ngắm san h&amp;ocirc;&amp;nbsp;&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;p&gt;Qu&amp;yacute; kh&amp;aacute;ch tắm nước ngọt v&amp;agrave; d&amp;ugrave;ng&amp;nbsp;&lt;strong&gt;bữa trưa với c&amp;aacute;c m&amp;oacute;n hải sản tươi sống&amp;nbsp;&lt;/strong&gt;do người d&amp;acirc;n l&amp;agrave;ng ch&amp;agrave;i vừa mới đ&amp;aacute;nh bắt được để chế biến cho qu&amp;yacute; kh&amp;aacute;ch thưởng thức&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;Tham quan&amp;nbsp;&lt;strong&gt;KDL Eo Gi&amp;oacute;&lt;/strong&gt;&amp;nbsp;&lt;em&gt;&amp;ndash; một ghềnh đ&amp;aacute; quanh năm lộng gi&amp;oacute;. Đứng tr&amp;ecirc;n Eo Gi&amp;oacute;, ph&amp;oacute;ng tầm mắt ra xa, qu&amp;yacute; kh&amp;aacute;ch chi&amp;ecirc;m ngưỡng bao qu&amp;aacute;t cả v&amp;ugrave;ng biển bao la rộng lớn, đắm say l&amp;ograve;ng người. Eo gi&amp;oacute; &amp;ndash; Nơi ngắm Ho&amp;agrave;ng h&amp;ocirc;n đẹp nhất Việt Nam.&amp;nbsp;&lt;/em&gt;&lt;/li&gt;\r\n	&lt;li&gt;&lt;strong&gt;Ch&amp;ugrave;a &amp;Ocirc;ng N&amp;uacute;i &amp;ndash; Tượng Phật ngồi lớn nhất Đ&amp;ocirc;ng Nam &amp;Aacute; tại B&amp;igrave;nh Định&lt;/strong&gt;&amp;nbsp;&lt;em&gt;- Tượng Đức Phật ngự tr&amp;ecirc;n t&amp;ograve;a sen, ở lưng chừng n&amp;uacute;i, tr&amp;ecirc;n độ cao 129 m so với mặt nước biển, nh&amp;igrave;n ra biển Đ&amp;ocirc;ng, lưng tựa v&amp;agrave;o ngọn n&amp;uacute;i cao nhất trong quần thể khu di t&amp;iacute;ch N&amp;uacute;i B&amp;agrave;. Dưới ch&amp;acirc;n tượng l&amp;agrave; Trung t&amp;acirc;m thuyết ph&amp;aacute;p Phật gi&amp;aacute;o v&amp;agrave; h&amp;agrave;nh lang La H&amp;aacute;n, thư viện Phật gi&amp;aacute;o, bảo t&amp;agrave;ng X&amp;aacute; Lợi Phật, nơi để du kh&amp;aacute;ch đến h&amp;agrave;nh lễ, chi&amp;ecirc;m b&amp;aacute;i.&amp;nbsp;&lt;/em&gt;&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;p&gt;Xe đưa đo&amp;agrave;n ra s&amp;acirc;n bay&amp;nbsp;&lt;strong&gt;Ph&amp;ugrave; C&amp;aacute;t&lt;/strong&gt;&amp;nbsp;l&amp;agrave;m thủ tục chuyến bay về th&amp;agrave;nh phố Hồ Ch&amp;iacute; Minh. Kết th&amp;uacute;c chương tr&amp;igrave;nh v&amp;agrave; hẹn gặp lại!&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;em&gt;&lt;strong&gt;(Giờ bay v&amp;agrave; lịch tr&amp;igrave;nh tham quan c&amp;oacute; thể thay đổi t&amp;ugrave;y v&amp;agrave;o t&amp;igrave;nh h&amp;igrave;nh thực tế của chuyến đi)&lt;/strong&gt;&lt;/em&gt;&lt;/p&gt;\r\n', '', 0, NULL, 1, 'PHÚ YÊN - QUY NHƠN (BÃI XẾP - GÀNH ĐÁ ĐĨA - LẶN NGẮM SAN HÔ ĐẢO KỲ CO - EO GIÓ) TGT', '', '', '{\"category\":{\"status\":\"\",\"sort\":\"\",\"display\":\"\",\"order_by\":\"\",\"number\":\"\"},\"brand\":{\"status\":\"\",\"sort\":\"\",\"display\":\"\",\"order_by\":\"\",\"number\":\"\"},\"price\":{\"status\":\"\",\"sort\":\"\",\"display\":\"\",\"order_by\":\"\",\"number\":\"\",\"range\":\"\"},\"select\":{\"status\":\"\",\"sort\":\"\",\"display\":\"\",\"order_by\":\"\",\"id_related\":\"\"}}', NULL, '&lt;p&gt;BẢNG GI&amp;Aacute;